  `comment_count` int DEFAULT NULL COMMENT '作品被评论的次数',
  PRIMARY KEY (`id`),
  KEY `t_works_t_species_id_fk` (`species_id`),
  KEY `t_works_t_user_id_fk` (`user_id`),
  CONSTRAINT `t_works_t_species_id_fk` FOREIGN KEY (`species_id`) REFERENCES `t_species` (`id`),
